mapper.select:
SELECT c.quality_tiem_id as qualityItemId,c.item_name as itemName,d.item_status as itemStatus,c.item_evidence_url as evidenceUrl, e.type_name as typeName,c.item_score as itemScore,c.quality_type_id as typeId FROM student_info as a INNER JOIN quality as b ON a.student_id = b.student_id
and b.academic_year = #{academicYear,jdbcType=VARCHAR}
INNER JOIN quality_item as c ON b.quality_id = c.quality_id INNER JOIN quality_item_audit as d ON c.quality_tiem_id = d.quality_item_id INNER JOIN item_type as e ON e.item_type_id = c.quality_type_id
a.student_id = #{studentId,jdbcType=BIGINT}

select 

from quality 

	
		student_id = #{studentId,jdbcType=BIGINT}
	
	
	 and academic_year = #{academicYear,jdbcType=VARCHAR}
	
	
	 and status = #{status,jdbcType=VARCHAR}

SELECT * FROM (   
	SELECT *,ROW_NUMBER() OVER(ORDER BY  #{sortOrder}) AS RowNumber from (
		SELECT a.quality_id,a.status,a.comprehensive_quality_score AS score,
		b.student_name AS studentName,b.student_id AS studentId,c.major_id AS majorId,
		major_name AS majorName,d.college_id AS collegeId,college_name AS collegeName
		FROM quality a
		LEFT JOIN student_info b ON a.student_id = b.student_id
		LEFT JOIN major c ON b.major_id = c.major_id
		LEFT JOIN college d ON c.college_id =d.college_id
		
			(b.student_name like '%'+#{key}+'%' or b.student_id like '%'+#{key}+'%')
			
        		and b.major_id = #{majorId}
      		
			
        		and c.college_id = #{collegeId}
			
			
				and b.student_id != #{studentId}
			
			
				and a.academic_year = #{academicYear}  
			
			
				and status = #{status}
			
		 
	) as T
)as T 

	T.RowNumber BETWEEN ((#{page}-1) * #{rows} + 1 )AND #{page}*#{rows}

		SELECT count(a.quality_id)
		FROM quality a
		LEFT JOIN student_info b ON a.student_id = b.student_id
		LEFT JOIN major c ON b.major_id = c.major_id
		LEFT JOIN college d ON c.college_id =d.college_id
		
			(b.student_name like '%'+#{key}+'%' or b.student_id like '%'+#{key}+'%')
			
        		and b.major_id = #{majorId}
      		
			
        		and c.college_id = #{collegeId}
			
			
				and b.student_id != #{studentId}
			
			
				and a.academic_year = #{academicYear}  
			
			
				and status =#{status}
